package com.shop.controller;import com.shop.pojo.VIP;import com.shop.service.VIPService;import org.apache.commons.logging.Log;import org.apache.commons.logging.LogFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;@Controllerpublic class VIPController {    @Autowired    private VIPService vipService;    @RequestMapping(value = "/addForm", method = RequestMethod.POST)    public String add( @ModelAttribute VIP vip, Model model){        vipService.addVIP(vip);        model.addAttribute("vip", vip);        return "/jsp/front/balance.jsp";    }    @RequestMapping(value = "/showBalance", method = RequestMethod.POST)    public String find(@ModelAttribute VIP vip, Model model){        vip = vipService.selBalance(vip.getPhone());        model.addAttribute("vip", vip);        return "/jsp/front/balance.jsp";    }}